import React, { Component } from 'react';
import { Trans, translate } from 'react-i18next';
import logo from './logo.svg';
import './App.css';
import DisplayCards from './components/display-cards/display-cards.js';
import Footer from './components/footer/footer.js';
import Hero from './components/hero/hero.js';
import MobileNav from './components/mobile-navbar/mobile-navbar.js';
import Nav from './components/navbar/navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
  render() {
    const { t, i18n } = this.props;

    const changeLanguage = lng => {
      console.log('in change lang', lng)
      i18n.changeLanguage(lng);
    };

    return (
      <div className="App">
        <div className="App-header">
          <button onClick={() => changeLanguage('jp')}>de</button>
          <button onClick={() => changeLanguage('en')}>en</button>
        </div>

        <Trans i18nKey='welcome.intro'>
            To get started, edit <code>src/App.js</code> and save to reload.
        </Trans>

        <Nav />
        <MobileNav />
        <Hero />
        <div className="card-container">
          <DisplayCards />    
        </div>
        <Footer />
      </div>
    );
  }
}
export default translate('common')(App);
