import React, { Component } from 'react';
import { translate } from "react-i18next";
import { LANGUAGES } from '../../config.js'


class LanguageSelector extends Component {

  constructor(props) {
    super(props)

    this.state = {dropdownBtn: LANGUAGES['en']}
  }

  render() {

      const { i18n } = this.props;

      const changeLanguage = lng => {
        i18n.changeLanguage(lng);
        this.setState({dropdownBtn: LANGUAGES[lng]})
    };

    return (
      <div class="dropdown">
        <button class="btn dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
         {this.state.dropdownBtn}
        </button>
        <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
          <div class="dropdown-item" onClick={() => changeLanguage('en')}>English (US)</div>
          <div class="dropdown-item" onClick={() => changeLanguage('jp')}>日本語</div>
        </div>
      </div>
    );
  } 
}


export default translate('common')(LanguageSelector);
